CREATE SEQUENCE IF NOT EXISTS bg_geometry_id_seq INCREMENT 1 START 20000 MINVALUE 1 MAXVALUE 9223372036854775807 CACHE 1;
CREATE SEQUENCE IF NOT EXISTS bg_task_user_rltn_id_seq INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 2147483647 CACHE 1;
CREATE TABLE IF NOT EXISTS bg_geometry2 (id integer NOT NULL DEFAULT nextval('bg_geometry_id_seq'::regclass), server_name text NOT NULL, data json, "time" timestamp without time zone DEFAULT timezone('utc'::text, now()), CONSTRAINT bg_geometry2_pkey PRIMARY KEY (id));
CREATE TABLE IF NOT EXISTS bg_missions (id integer NOT NULL DEFAULT nextval('bg_geometry_id_seq'::regclass), server_name text NOT NULL, "time" timestamp without time zone NOT NULL DEFAULT timezone('utc'::text, now()), data json, CONSTRAINT bg_missions_pkey PRIMARY KEY (id));
CREATE TABLE IF NOT EXISTS bg_task (id integer NOT NULL DEFAULT nextval('bg_geometry_id_seq'::regclass), id_mission integer NOT NULL, server_name text NOT NULL, "time" timestamp without time zone NOT NULL DEFAULT timezone('utc'::text, now()), data json, CONSTRAINT bg_task_pkey PRIMARY KEY (id));
CREATE TABLE IF NOT EXISTS bg_task_user_rltn (id integer NOT NULL DEFAULT nextval('bg_task_user_rltn_id_seq'::regclass), id_task integer NOT NULL, discord_id bigint NOT NULL, "time" timestamp without time zone NOT NULL DEFAULT timezone('utc'::text, now()), CONSTRAINT bg_task_user_rltn_pkey PRIMARY KEY (id));
